#title-aside,aside #green h2,aside #green h3,aside h3{text-transform:uppercase}#title-aside{color:#404719;font-size:23px;font-family:Roboto,sans-serif;font-weight:700;padding:20px 15px 0}aside #green{padding-top:15px}ul{list-style:none}a,aside,body,div,h1,h2,h3,header,html,iframe,img,li,nav,p,section,strong,ul{margin:0;padding:0;border:0;font:inherit;vertical-align:baseline}aside h3,body,section#contenuto h1,strong{font-family:Roboto,sans-serif}.container,body,header{margin:0 auto}aside,header,nav,section{display:block}body{line-height:1;background-color:#FFF;font-size:13px;color:#333}.clear{clear:both}strong{font-weight:700}.clearfix:after{content:".";display:block;clear:both;visibility:hidden;line-height:0;height:0}header{height:90px;background-color:#333}.container{width:1200px}img.logo{margin:15px 0}nav ul{text-align:left;display:inline;margin:0;padding:15px 4px 17px 0;z-index:1}section#contenuto h1{font-size:28px;color:#474747;font-weight:700}.clearfix{height:20px}section#contenuto h3{font-size:16px;font-weight:700;line-height:30px;margin-top:15px}section#contenuto a{color:#35a6e8;text-decoration:underline;font-weight:700}section#contenuto{line-height:1.6;float:right;width:895px;moz-box-sizing:border-box;-webkit-box-sizing:border-box;box-sizing:border-box}aside h3{color:#FFF;font-weight:700;font-size:34px}#popup_map{display:none;height:100%;width:80%;margin-top:30px;background-color:transparent}.close_button{position:absolute;border:0;z-index:100;margin-top:-18px;right:-15px}.panel-body{font-family:Roboto,Helvetica,Arial,sans-serif;margin:0 auto;padding:15px;font-size:15px;line-height:1.6;color:#444}.panel-body p{padding-bottom:7px}.panel-body ul{margin:23px 30px 30px!important;display:block;list-style-type:disc}.panel-body .barusso ul{margin:0!important}input{font-family:inherit;font-size:inherit;line-height:inherit}.barusso{margin-top:20px}.barusso ul li{display:inline-block;background-color:#f2f4f8;margin:0 10px 8px 0}.barusso ul li a{color:#333;text-decoration:none!important;padding:16px 10px;line-height:50px}section#contenuto .no_risultati_ricerca img{width:initial;height:initial;margin:initial}aside #green h2,aside #green h3,aside #green ul li{font-weight:700;font-family:Roboto,sans-serif}aside #green h2,aside #green h3,aside #green ul li,aside #green ul li span{font-family:Roboto,sans-serif}aside#sidebar{float:left;width:290px}aside #green{background-color:#cde942}aside #green h2{color:#404719;font-size:23px;padding:20px 15px 0}aside #green h3{color:#b20000;font-size:15px;padding:30px 15px 5px}aside #green ul li{font-size:13px;color:#404719;margin:0}aside #green ul li a{display:block;color:#404719;text-decoration:none;padding:5px 15px}aside #green ul li a:hover{background-color:#e3f987}.shareaholic-canvas{float:right;top:0!important;width:30%;margin-top:-3px}.no_risultati_ricerca{padding:40px 0;font-size:25px;color:red;line-height:30px;width:80%;margin:10px auto;text-align:center}.servizio_ricerca_avanzata_container{display:none}@media screen and (max-device-width:1200px){body,header{width:1200px}}