#boxspot,#boxspot a:after{position:absolute;width:596px}#boxspot a:after,.watermark:after{content:" ";left:0;top:0}#boxspot ul,#fasciablu,#logo,.numhotel,.prezzohome,div.watermark h2{text-align:center}#boxspot{top:0;right:4px;background:url(/js/fancybox/fancybox_loading.gif) center center no-repeat #fff;height:456px;overflow:hidden}#boxspot a{display:block;text-decoration:none;line-height:1.5em;color:#222;position:relative;height:456px;float:left}#boxspot a:after{background:url(/images/pellicola.png);height:290px;z-index:0}#boxspot a.big,#boxspot a.big:after{height:456px}#boxspot .boxspot_image{height:290px;overflow-x:hidden}#boxspot .boxspot_title{font-size:30px;padding:0 10px 40px;color:#fff;position:relative;z-index:2;text-shadow:0 1px rgba(0,0,0,.5)}#boxspot .boxspot_date{padding:0 10px 10px;position:relative;margin-top:-80px;z-index:2}#boxspot .boxspot_date span{font-size:12px;color:#222;background:rgba(255,255,255,.7);display:inline-block;padding:2px 7px}#homebox a,#homelocalita>a,article,aside,details,figcaption,figure,footer,header,hgroup,menu,nav,section{display:block}#boxspot .boxspot_text{font-size:14px;padding:0 10px 10px;line-height:22px}#boxspot .boxspot_text b{font-weight:700}#boxspot ul{position:absolute;bottom:10px;left:0;right:0;width:auto}#boxspot ul,#boxspot ul li{padding:0;margin:0;display:inline-block}#boxspot ul li button{background:#ddd;margin:0 5px;font-size:1px;border:2px solid #fff;width:16px;height:16px;display:block;color:transparent;-webkit-border-radius:50%;-moz-border-radius:50%;border-radius:50%}#boxspot ul li.slick-active button{border:2px solid rgba(0,0,0,.2)}.container,body,header{margin:0 auto}#newshome1 a,#newshome1 h3,#newshome2 a,#newshome2 h3,.numhotel,.prezzohome,div.watermark h2{font-weight:700;text-transform:uppercase}#fasciablu h1{margin:0}#fasciablu h1,#newshome1 a,#newshome1 h3,#newshome2 a,#newshome2 h3,.numhotel,.prezzohome,div.watermark h2{text-transform:uppercase}body{line-height:1;font-family:Roboto,sans-serif;font-size:13px;color:#333;background-color:#fff}header{height:90px;background-color:#333}.container{width:1200px}#fasciablu,#logo{width:100%}#logo{padding-top:15px}img.logo{margin:15px 0}#fasciablu{height:60px;background-color:#35a6e8;line-height:60px}#homelocalita>a,.watermark{width:294px;height:224px;float:left}#fasciablu h1{color:#FFF;font-size:20px}#homelocalita{margin:0 -4px;position:relative}#homelocalita>a{outline:0;margin:0 4px 8px;overflow:hidden}.watermark{position:relative;z-index:-1}.watermark:after{background:url(//static.info-alberghi.com/images/pellicola.png) rgba(0,0,0,0);bottom:0;position:absolute;right:0;z-index:-1;-webkit-transition:background .25s ease-in-out;-moz-transition:background .25s ease-in-out;-ms-transition:background .25s ease-in-out;-o-transition:background .25s ease-in-out;transition:background .25s ease-in-out}a:hover .watermark:after{background:url(//static.info-alberghi.com/images/pellicola.png) rgba(0,0,0,.5)}div.watermark{margin-bottom:9px}div.watermark h2{font-size:26px;color:#fff;z-index:999;margin-top:85px}.numhotel{font-size:18px;color:#fff;margin-top:5px;margin-bottom:35px}.prezzohome{font-size:15px;color:#fff;border:1px solid #fff;padding:7px;width:70%;margin:0 auto}#homebox,#homebox img{margin-bottom:30px}#homebox a{text-decoration:none;float:left}#homebox img{float:left;margin-right:40px}.benessere,.bimbi,.couponsconto,.fierarimini,.greenbooking,.lastmin,.legambiente,.offerte,.petfriendly,.piscina{width:476px;height:66px;margin-bottom:9px;color:#fff;padding:48px 60px;font-size:16px;line-height:1.3em}.benessere h3,.bimbi h3,.couponsconto h3,.fierarimini h3,.greenbooking h3,.lastmin h3,.legambiente h3,.offerte h3,.petfriendly h3,.piscina h3{color:#fff;font-size:24px;margin-bottom:10px;text-transform:uppercase;font-weight:700}.bimbi{background-color:#1fb0e1}.benessere{background-color:#ec407a}.couponsconto{background-color:#575f6b}.offerte{background-color:#26a69a}.piscina{background-color:#f1b000}.lastmin{background-color:#f24