var Tools={init:function(){$("body").prepend('<a href="#" class="back-to-top"><span>TOP</span></a>');var o=300;$("a.back-to-top, a.simple-back-to-top").click(function(){return $("html, body").animate({scrollTop:0},700),!1}),$(window).scroll(function(){$(window).scrollTop()>o?$("a.back-to-top").fadeIn("slow"):$("a.back-to-top").fadeOut("slow")})}};Tools.init();